import React, {useState,useContext} from 'react'
import axios from 'axios'
import { GlobalState } from "../../../GlobalState"
import "./CreateProduct.css"
const initialState ={
    product_id:'',
    title:'',
    price:0,
    description:'hehe =))))',
    content:'hihi =))))',
    category:''
}
function CreateProduct() {
    const[product,setProduct] = useState(initialState)
    const state= useContext(GlobalState)
    const [categories]=state.categoriesAPI.categories
    const [isAdmin] = state.userAPI.isAdmin
    const [images, setImages] = useState(false)
    const [token] = state.token
    const styleUpload ={
        display: images ? 'block' : 'none'
    }
    const handleUpload = async (e) => {
        e.preventDefault();
        try {
            if (!isAdmin) return alert("You are not an Admin");
            const file = e.target.files[0];
            if (!file) return alert("File not exist");
    
            let formData = new FormData();
            formData.append('file', file);
    
            const res = await axios.post('/api/upload', formData, {
                headers: { 'content-type': 'multipart/form-data', Authorization: token }
            });
            setImages(res.data);
        } catch (err) {
            alert(err.response.data.msg);
        }
    }
    const handleDestroy= async (e) =>{
        try {
            if(!isAdmin) return alert('You are not admin')
            await axios.post('/api/destroy',{public_id: images.public_id},{
                headers: {Authorization : token}
        })
        setImages(false)
        } catch (err) {
            alert(err.response.data.msg)
        }
    }
    return (
        <div className="create_product">
            <div className="upload">
                <input type="file" name="file" id="file_up" onChange={handleUpload}/>
                <div id="file_img" style={styleUpload}>
                    <img src={images ? images.url: ''} alt=""/>
                    <span onClick={handleDestroy}>x</span>
                </div>
            </div>
            <form>
                <div className="row">
                    <label htmlFor="product_id">Product_id</label>
                    <input type="text" name="product_id" id="product_id" 
                    required value={product.product_id}/>
                </div>
                <div className="row">
                    <label htmlFor="Title">Title</label>
                    <input type="text" name="title" id="titled" 
                    required value={product.title}/>
                </div>
                <div className="row">
                    <label htmlFor="price">Price</label>
                    <input type="number" name="price" id="price" 
                    required value={product.price}/>
                </div>
                <div className="row">
                    <label htmlFor="description">Description</label>
                    <textarea rows="5" name="description" id="description" 
                    required value={product.description}/>
                </div>
                <div className="row">
                    <label htmlFor="content">Content</label>
                    <textarea rows="7" type="text" name="content" id="content" 
                    required value={product.content}/>
                </div>
                <div className="row">
                    <label htmlFor="category">Category</label>
                    <select name="category" id="category" 
                    value={product.category}>
                        <option value="">Please select a category</option>
                        {
                            categories.map(category => (
                                <option key={category._id} value={category._id}>
                                    {category.name}
                                </option>
                            ))
                        }
                    </select>
                </div>
                <button type="submit">Create Product</button>
            </form>
        </div>
    )
}

export default CreateProduct
