import React, { createContext, useEffect, useState } from 'react';   
import axios from 'axios';
import ProductsAPI from "./api/ProductsAPI";
import UserAPI from './api/UserAPI';
import CategoriesAPI from './api/CategoriesAPI';

export const GlobalState = createContext();

export const DataProvider = ({ children }) => {
    const [token, setToken] = useState(false);
    const [products, setProducts] = useState([]);  // Giả sử bạn muốn lưu trữ danh sách sản phẩm ở đây

    const refreshToken = async () => {
        const res = await axios.get('/user/refresh_token');
        setToken(res.data.accesstoken);
    };

    useEffect(() => {
        const firstLogin = localStorage.getItem('firstLogin');
        if (firstLogin) refreshToken();
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        const { data } = await axios.get('/api/products');  // Giả sử bạn có một endpoint như này
        setProducts(data);
    };

    const updateProducts = async () => {
        fetchProducts();  // Làm mới danh sách sản phẩm
    };

    const state = {
        token: [token, setToken],
        productsAPI: { products, updateProducts },
        userAPI: UserAPI(token),
        categoriesAPI: CategoriesAPI(token)
    };

    return (
        <GlobalState.Provider value={state}>
            {children}
        </GlobalState.Provider>
    );
};
