import React from "react";
import { Link } from "react-router-dom";
import "./ProductItem.css";

function ProductItem({ product, isAdmin, addCart }) { // Truyền addCart trực tiếp
  return (
    <div className="product_card">
      {isAdmin && <input type="checkbox" checked={product.checked} />}
      <img src="https://res.cloudinary.com/dib4uvdkl/image/upload/v1712765044/test/ozdpewayi6kbk56p3f65.jpg" alt="" />

      <div className="product_box">
        <h2>{product.title}</h2>
        <h1>${product.price}</h1>
        <p>{product.description}</p>
      </div>
      <div className="row_btn">
        {isAdmin ? (
          <>
            <Link id="btn_buy" to="#!">
              DELETE
            </Link>
            <Link id="btn_view" to={`/edit_product/${product._id}`}>
              EDIT
            </Link>
          </>
        ) : (
          <>
            <Link id="btn_buy" to="#!" onClick={() => addCart(product)}> {/* Sử dụng addCart trực tiếp */}
              Add to Cart
            </Link>
            <Link id="btn_view" to={`/detail/${product._id}`}>
              Detail
            </Link>
          </>
        )}
      </div>
    </div>
  );
}

export default ProductItem;
